import React, { Component } from 'react';

class BackgroundChanger extends Component {
  constructor(props) {
    super(props);
    this.state = { bgColor: '#fff' };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    const colors = ['#f00', '#0f0', '#00f', '#ff0', '#0ff', '#f0f', '#ff'];
    const newColor = colors[Math.floor(Math.random() * colors.length)];
    this.setState({ bgColor: newColor });
  }

  render() {
    return (
      <div className= "color-container" style={{ backgroundColor: this.state.bgColor }}>
        <h1>Tryck på knappen för att ändra färg</h1>
        <button onClick={this.handleClick}>Ändra färg</button>
      </div>
    );
  }
}

export default BackgroundChanger;
